SET SERVEROUTPUT ON;
DECLARE

    ERROR_TABLE_NAME VARCHAR2(50);

BEGIN

    ERROR_TABLE_NAME := 'vasapps.ZND_E_NOTICE_TYPE';

    ERROR_TABLE_NAME := 'vasapps.ZND_E_NOTICE_VARIABLE_TYPE';


    COMMIT;

    DBMS_OUTPUT.PUT_LINE('SCRIPT COMPLETE');
EXCEPTION
    WHEN DUP_VAL_ON_INDEX THEN
        ROLLBACK;
        DBMS_OUTPUT.PUT_LINE('DUPLICATE PRIMERY KEY EXCEPTION~'||SQLERRM);
        DBMS_OUTPUT.PUT_LINE('IN TABLE~'||ERROR_TABLE_NAME);
    WHEN OTHERS THEN
        ROLLBACK;
        DBMS_OUTPUT.PUT_LINE('EXCEPTION IN INSERT STATEMENT~'||SQLERRM);
        DBMS_OUTPUT.PUT_LINE('IN TABLE~'||ERROR_TABLE_NAME);
END;
/
